    // Update is called once per frame
    void Update() {
        LinkedList<WarFog_CheckPoint> dynamicCheckPoint = new LinkedList<WarFog_CheckPoint>();
        var sPoint1 = ScriptObjectPool<WarFog_CheckPoint>.getInstance().getPoolObject();
        int cameraX = (int)CameraTrans.position.x;
        int cameraY = (int)CameraTrans.position.z;
        sPoint1.setCheckPoint(cameraX, cameraY, 21);

        if (sPoint1.X >= 0 && sPoint1.X < Width && sPoint1.Y >= 0 && sPoint1.Y < Height) {
            dynamicCheckPoint.AddLast(sPoint1);
        } else {
            ScriptObjectPool<WarFog_CheckPoint>.getInstance().pushPoolObject(sPoint1);
        }

        WarFogApi.getInstance().WarFogCaculater.setDynamicCheckPoints(dynamicCheckPoint);

        for (int y = 0; y < Height; ++y) {
            for (int x = 0; x < Width; ++x) {
                if (WarFogApi.getInstance().WarFogCaculater.CurrentFogData[x, y]) {
                    fogData[y, x] = 1;
                } else {
                    fogData[y, x] = 0;
                }
            }
        }
        WarFogApi.getInstance().PlaneDisplayer.setFogData(fogData);

        if (Input.GetKey(KeyCode.W)) {
            CameraTrans.position = new Vector3(CameraTrans.position.x, CameraTrans.position.y, CameraTrans.position.z + cameraMoveOffset);
        } else if (Input.GetKey(KeyCode.A)) {
            CameraTrans.position = new Vector3(CameraTrans.position.x - cameraMoveOffset, CameraTrans.position.y, CameraTrans.position.z);
        } else if (Input.GetKey(KeyCode.S)) {
            CameraTrans.position = new Vector3(CameraTrans.position.x, CameraTrans.position.y, CameraTrans.position.z - cameraMoveOffset);
        } else if (Input.GetKey(KeyCode.D)) {
            CameraTrans.position = new Vector3(CameraTrans.position.x + cameraMoveOffset, CameraTrans.position.y, CameraTrans.position.z);
        }

        WarFogApi.getInstance().PlaneDisplayer.updateFog();
    }
